import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Navbar as BootstrapNavbar, Nav, Container } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome, faFileUpload, faFileAlt, faBriefcase, faSearch } from '@fortawesome/free-solid-svg-icons';

const Navbar = () => {
  const location = useLocation();

  const isActive = (path) => {
    return location.pathname === path ? 'active' : '';
  };

  return (
    <BootstrapNavbar expand="lg" className="navbar-custom">
      <Container>
        <BootstrapNavbar.Brand as={Link} to="/" className="d-flex align-items-center">
          <FontAwesomeIcon icon={faFileAlt} className="me-2" />
          <span>ResumeRAG</span>
        </BootstrapNavbar.Brand>
        <BootstrapNavbar.Toggle aria-controls="basic-navbar-nav" />
        <BootstrapNavbar.Collapse id="basic-navbar-nav">
          <Nav className="ms-auto">
            <Nav.Link as={Link} to="/" className={isActive('/')}>
              <FontAwesomeIcon icon={faHome} className="me-1" /> Home
            </Nav.Link>
            <Nav.Link as={Link} to="/upload-resume" className={isActive('/upload-resume')}>
              <FontAwesomeIcon icon={faFileUpload} className="me-1" /> Upload Resume
            </Nav.Link>
            <Nav.Link as={Link} to="/resumes" className={isActive('/resumes')}>
              <FontAwesomeIcon icon={faFileAlt} className="me-1" /> Resumes
            </Nav.Link>
            <Nav.Link as={Link} to="/add-job" className={isActive('/add-job')}>
              <FontAwesomeIcon icon={faBriefcase} className="me-1" /> Add Job
            </Nav.Link>
            <Nav.Link as={Link} to="/jobs" className={isActive('/jobs')}>
              <FontAwesomeIcon icon={faSearch} className="me-1" /> Jobs
            </Nav.Link>
          </Nav>
        </BootstrapNavbar.Collapse>
      </Container>
    </BootstrapNavbar>
  );
};

export default Navbar;
