import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Card, Button, Form, InputGroup, Row, Col, Badge, Accordion } from 'react-bootstrap';
import axios from 'axios';

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [showAdvancedSearch, setShowAdvancedSearch] = useState(false);
  const [advancedFilters, setAdvancedFilters] = useState({
    title: '',
    company: '',
    location: '',
    minSalary: '',
    maxSalary: '',
    jobType: '',
    skills: '',
    experienceLevel: ''
  });

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const response = await axios.get('/api/jobs');
      setJobs(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setError('Failed to load jobs. Please try again later.');
      setLoading(false);
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();

    if (!searchTerm.trim()) {
      fetchJobs();
      return;
    }

    try {
      setLoading(true);
      const response = await axios.get(`/api/jobs/search/${searchTerm}`);
      setJobs(response.data);
    } catch (error) {
      console.error('Error searching jobs:', error);
      setError('Failed to search jobs. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleAdvancedSearch = async (e) => {
    e.preventDefault();

    try {
      setLoading(true);

      // Build query parameters
      const params = new URLSearchParams();

      Object.entries(advancedFilters).forEach(([key, value]) => {
        if (value) params.append(key, value);
      });

      const response = await axios.get(`/api/jobs/search/advanced?${params.toString()}`);
      setJobs(response.data);
    } catch (error) {
      console.error('Error in advanced job search:', error);
      setError('Failed to search jobs. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setAdvancedFilters(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this job?')) {
      try {
        await axios.delete(`/api/jobs/${id}`);
        setJobs(jobs.filter(job => job._id !== id));
      } catch (error) {
        console.error('Error deleting job:', error);
        setError('Failed to delete job. Please try again later.');
      }
    }
  };

  if (loading) {
    return <div className="text-center my-5">Loading jobs...</div>;
  }

  if (error) {
    return <div className="alert alert-danger my-3">{error}</div>;
  }

  return (
    <div>
      <h2 className="mb-4">Job Listings</h2>

      <Form onSubmit={handleSearch} className="mb-4">
        <InputGroup>
          <Form.Control
            type="text"
            placeholder="Search jobs by title, skills, or description"
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
          <Button type="submit" variant="primary">Search</Button>
          {searchTerm && (
            <Button 
              variant="outline-secondary" 
              onClick={() => {
                setSearchTerm('');
                fetchJobs();
              }}
            >
              Clear
            </Button>
          )}
        </InputGroup>
      </Form>

      {jobs.length === 0 ? (
        <div className="alert alert-info">No jobs found.</div>
      ) : (
        <Row>
          {jobs.map(job => (
            <Col md={6} lg={4} className="mb-4" key={job._id}>
              <Card className="h-100">
                <Card.Body>
                  <Card.Title>{job.title}</Card.Title>
                  <Card.Subtitle className="mb-2 text-muted">{job.company}</Card.Subtitle>

                  <div className="mb-2">
                    <Badge bg="primary" className="me-1">{job.jobType}</Badge>
                    {job.location && <Badge bg="secondary">{job.location}</Badge>}
                  </div>

                  <Card.Text className="mb-2">
                    {job.description.length > 100 
                      ? `${job.description.substring(0, 100)}...` 
                      : job.description}
                  </Card.Text>

                  {job.skills && job.skills.length > 0 && (
                    <div className="mb-2">
                      <small className="text-muted">Skills:</small><br />
                      {job.skills.map((skill, index) => (
                        <Badge bg="info" className="me-1 mb-1" key={index}>{skill}</Badge>
                      ))}
                    </div>
                  )}

                  <div className="d-flex justify-content-between mt-3">
                    <Button 
                      as={Link} 
                      to={`/match/${job._id}`} 
                      variant="success" 
                      size="sm"
                    >
                      Find Matches
                    </Button>
                    <Button 
                      variant="danger" 
                      size="sm" 
                      onClick={() => handleDelete(job._id)}
                    >
                      Delete
                    </Button>
                  </div>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      )}
    </div>
  );
};

export default JobList;
