import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Card, Button, Form, InputGroup, Row, Col, Badge, Accordion, Alert } from 'react-bootstrap';
import axios from 'axios';

const JobList = () => {
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    jobType: '',
    location: '',
    experienceLevel: ''
  });

  useEffect(() => {
    fetchJobs();
  }, []);

  const fetchJobs = async () => {
    try {
      const response = await axios.get('/api/jobs');
      setJobs(response.data);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching jobs:', error);
      setError('Failed to load jobs. Please try again later.');
      setLoading(false);
    }
  };

  const handleSearch = (e) => {
    e.preventDefault();
    if (searchTerm.trim()) {
      setLoading(true);
      axios.get(`/api/jobs/search/${searchTerm}`)
        .then(response => {
          setJobs(response.data);
          setLoading(false);
        })
        .catch(err => {
          console.error('Error searching jobs:', err);
          setError('Failed to search jobs. Please try again later.');
          setLoading(false);
        });
    } else {
      fetchJobs();
    }
  };

  const handleFilterChange = (field, value) => {
    setFilters({
      ...filters,
      [field]: value
    });
  };

  const toggleFilters = () => {
    setShowFilters(!showFilters);
  };

  const handleDelete = (id) => {
    if (window.confirm('Are you sure you want to delete this job?')) {
      axios.delete(`/api/jobs/${id}`)
        .then(() => {
          setJobs(jobs.filter(job => job._id !== id));
        })
        .catch(err => {
          console.error('Error deleting job:', err);
          setError('Failed to delete job. Please try again later.');
        });
    }
  };

  const handleEdit = (id) => {
    // Navigation handled by Link component
  };

  const handleViewDetails = (job) => {
    // Navigation handled by Link component
  };

  // Apply filters
  const filteredJobs = jobs.filter(job => {
    return (
      (filters.jobType === '' || job.jobType === filters.jobType) &&
      (filters.location === '' || job.location.toLowerCase().includes(filters.location.toLowerCase())) &&
      (filters.experienceLevel === '' || job.experienceLevel === filters.experienceLevel)
    );
  });

  return (
    <div className="job-list-container">
      <h2 className="mb-4">Job Listings</h2>

      <Form onSubmit={handleSearch} className="mb-4">
        <Row>
          <Col md={8}>
            <InputGroup>
              <Form.Control
                type="text"
                placeholder="Search jobs by title, company, or skills..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="search-input"
              />
              <Button variant="primary" type="submit">
                Search
              </Button>
            </InputGroup>
          </Col>
          <Col md={4}>
            <Button 
              variant="outline-secondary" 
              onClick={toggleFilters}
              className="w-100"
            >
              {showFilters ? 'Hide Filters' : 'Show Filters'}
            </Button>
          </Col>
        </Row>

        {showFilters && (
          <Row className="mt-3">
            <Col md={4}>
              <Form.Group>
                <Form.Label>Job Type</Form.Label>
                <Form.Control 
                  as="select" 
                  value={filters.jobType} 
                  onChange={(e) => handleFilterChange('jobType', e.target.value)}
                >
                  <option value="">All Types</option>
                  <option value="Full-time">Full-time</option>
                  <option value="Part-time">Part-time</option>
                  <option value="Contract">Contract</option>
                  <option value="Internship">Internship</option>
                </Form.Control>
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group>
                <Form.Label>Location</Form.Label>
                <Form.Control 
                  type="text" 
                  placeholder="Filter by location" 
                  value={filters.location} 
                  onChange={(e) => handleFilterChange('location', e.target.value)}
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group>
                <Form.Label>Experience Level</Form.Label>
                <Form.Control 
                  as="select" 
                  value={filters.experienceLevel} 
                  onChange={(e) => handleFilterChange('experienceLevel', e.target.value)}
                >
                  <option value="">All Levels</option>
                  <option value="Entry">Entry Level</option>
                  <option value="Mid">Mid Level</option>
                  <option value="Senior">Senior Level</option>
                  <option value="Executive">Executive Level</option>
                </Form.Control>
              </Form.Group>
            </Col>
          </Row>
        )}
      </Form>

      {loading ? (
        <div className="text-center my-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
        </div>
      ) : error ? (
        <Alert variant="danger">{error}</Alert>
      ) : filteredJobs.length === 0 ? (
        <Alert variant="info">No jobs found. Try adjusting your search criteria.</Alert>
      ) : (
        <div className="job-cards">
          {filteredJobs.map((job) => (
            <Card key={job._id} className="job-card mb-4">
              <Card.Body>
                <Row>
                  <Col md={8}>
                    <div className="job-card-content">
                      <h3 className="job-title">{job.title}</h3>
                      <h5 className="company-name">{job.company}</h5>
                      <div className="job-meta">
                        <span className="job-location me-3">{job.location}</span>
                        <span className="job-type me-3">{job.jobType}</span>
                        <span className="job-salary">{job.salary ? `$${job.salary.toLocaleString()}` : 'Not specified'}</span>
                      </div>
                      <div className="skills-container mt-2">
                        {job.skills && job.skills.map((skill, index) => (
                          <Badge key={index} bg="primary" className="skill-badge me-1">{skill}</Badge>
                        ))}
                      </div>
                      <div className="mt-3 description-container">
                        {job.description && (
                          <div>
                            <strong>Description:</strong>
                            <div 
                              className="job-description"
                              dangerouslySetInnerHTML={{ 
                                __html: job.description.length > 300 
                                  ? job.description.substring(0, 300) + '...' 
                                  : job.description 
                              }} 
                            />
                          </div>
                        )}
                      </div>
                    </div>
                  </Col>
                  <Col md={4} className="d-flex flex-column justify-content-between">
                    <div>
                      <p className="posted-date">
                        <strong>Posted:</strong> {new Date(job.createdAt).toLocaleDateString()}
                      </p>
                      <p className="requirements">
                        <strong>Requirements:</strong> {job.requirements ? job.requirements.substring(0, 100) + '...' : 'Not specified'}
                      </p>
                    </div>
                    <div className="mt-3 d-flex flex-column action-buttons">
                      <Link to={`/jobs/${job._id}`} className="btn btn-primary mb-2">View Details</Link>
                      <Link to={`/edit-job/${job._id}`} className="btn btn-secondary mb-2">Edit</Link>
                      <Button variant="danger" onClick={() => handleDelete(job._id)}>Delete</Button>
                    </div>
                  </Col>
                </Row>
              </Card.Body>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default JobList;
